func (rus *ResourceUserSelect) sqlScan(ctx context.Context, v interface{}) error {
	rows := &sql.Rows{}
	query, args := rus.sqlQuery().Query()
	if err := rus.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (rus *ResourceUserSelect) sqlQuery() sql.Querier {
	selector := rus.sql
	selector.Select(selector.Columns(rus.fields...)...)
	return selector
}
