func (tcs *TagCommonSelect) sqlScan(ctx context.Context, v interface{}) error {
	rows := &sql.Rows{}
	query, args := tcs.sqlQuery().Query()
	if err := tcs.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (tcs *TagCommonSelect) sqlQuery() sql.Querier {
	selector := tcs.sql
	selector.Select(selector.Columns(tcs.fields...)...)
	return selector
}
